        //Jeremy: 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
        //Ronnie: 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
        public static readonly SendGridClient Client = new("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");

        public static Task SendEmailFreeConsultationAsync(EmailContact contact, string subject, string message)
        {
            var from = new EmailAddress("studentathlete@isportsrecruiting.com", $"{contact.Name}");

            return SendEmailAsync(from, contact, subject, message);
        }

        public static async Task SendEmailAsync(EmailAddress from, EmailContact contact, string subject, string message)
        {
            try
            {
                var to = new EmailAddress(contact.Email, contact.Name);
                var msg = MailHelper.CreateSingleEmail(from, to, subject, "", message);

                await Client.SendEmailAsync(msg);
            }
            catch (Exception e)
            {

                throw new Exception(e.Message);
            }
        }

        public static async Task SendEmailAsHtmlAsync(EmailAddress from, EmailContact contact, string subject, string message)
        {
            var to = new EmailAddress(contact.Email, contact.Name);
            var msg = MailHelper.CreateSingleEmail(from, to, subject, "", htmlContent: message);

            await Client.SendEmailAsync(msg);
        }
    }

